import React, { useState } from 'react';

function Header() {
    const [fruits, setFruits] = useState({
        brand: "Ford",
        model: "Mustang",
        year: "1964",
        color: "red"
    });

    const handerColor = () => {
        setFruits(previousState => {
            return {
                ...previousState, color: "green"
            }
        });
    }
    return (
        <div>
            <ul>
                {fruits.map((item) => <li brand={item}>{item}</li>)}
            </ul>
            <button type="button" onClick={() => handerColor()}>click me!</button>
        </div>
    )
}

export default Header;
